export default interface IObservable {
    on(type: string, callback: Function, scope?: any, priority?: number): IObservable;
    once(type: string, callback: Function, scope?: any, priority?: number): IObservable;
    off(type: string, callback: Function, scope?: any): IObservable;
    trigger(type: any, data?: any): IObservable;
    has(type: string): boolean;
}
