import EventDispatcher from '../../../../../event/EventDispatcher';
/**
 * `Layer` is a container class that is responsible for creating and managing a OpenLayers
 * source and associated layer representation on a OpenLayers map.
 */
export default class OpenLayersLayer extends EventDispatcher {
    id: string;
    source: any;
    layer: ol.layer.Layer;
    private _idPrefix;
    private _map;
    private _hidden;
    private _relativeTo;
    protected _targetIndex: number;
    constructor(layer: string, id?: string);
    /**
     * Layer identifier on the map.
     *
     * @readonly
     * @type {string}
     * @memberof OpenLayersLayer
     */
    readonly layerId: string;
    /**
     * Map currently associated with the layer.
     *
     * @readonly
     * @type {mapboxgl.Map}
     * @memberof Layer
     */
    readonly map: ol.Map;
    /**
     * Returns whether the layer is currently hidden.
     *
     * @readonly
     * @type {boolean}
     * @memberof Layer
     */
    readonly isHidden: boolean;
    /**
     * Adds the layer to the specified map.
     *
     * @param {mapboxgl.Map} map
     * @param {number} index
     * @memberof Layer
     */
    addTo(map: ol.Map, index?: number): void;
    /**
     * Removes the layer from the map it is currently active on.
     *
     * @returns
     * @memberof Layer
     */
    remove(): void;
    /**
     * Shows the layer on the map.
     *
     * @memberof Layer
     */
    show(): void;
    /**
     * Hides the layer on the map.
     *
     * @memberof Layer
     */
    hide(): void;
    /**
     * Changes the opacity of the layer.
     *
     * @param {number} value
     * @memberof Layer
     */
    setOpacity(value: number): void;
    setZIndex(index: number): void;
    /**
     * Returns whether the layer currently exists on the map.
     *
     * @returns {boolean}
     * @memberof Layer
     */
    exists(): boolean;
    /**
     * Forces a redraw of the layer on the map.
     *
     * @returns
     * @memberof Layer
     */
    invalidate(): void;
}
