import OpenLayersLayer from './Layer';
import { ICoordinateBounds } from '../../../../../interfaces/ICoordinate';
/**
 * `TileLayer` is a container class that is responsible for creating and managing a OpenLayers
 * source and associated layer representation on a OpenLayers map.
 */
export default class OpenLayersTileLayer extends OpenLayersLayer {
    private _source;
    private _type;
    private _opts;
    private _loadCounter;
    constructor(layer: string, type?: string, id?: string);
    /**
     * Updates the tile or image url for the layer.
     *
     * @param {string} url
     * @memberof TileLayer
     */
    setUrl(url: string): void;
    /**
     * Updates the bounds for the layer when rendered as an image.
     *
     * @param {ICoordinateBounds} bounds
     * @memberof TileLayer
     */
    setBounds(bounds: ICoordinateBounds): void;
    /**
     * Forces a redraw of the layer on the map.
     *
     * @returns
     * @memberof TileLayer
     */
    invalidate(): void;
}
