export default interface IAnimatable {
    toggle(): void;
    play(): void;
    pause(): void;
    stop(): void;
    restart(): void;
    reset(): void;
    goToTime(time: any): IAnimatable;
    setStartDate(date: Date | number): IAnimatable;
    setStartOffset(offset: number): IAnimatable;
    setEndDate(date: Date | number): IAnimatable;
    setEndOffset(offset: number): IAnimatable;
}
