/**
 * An interface that is implemented by a class or type that contains style configuration
 * information.
 *
 * @export
 * @interface IStyleable
 */
export default interface IStyleable {
    /**
     * Style options, which will vary depending on the object class or type.
     *
     * @type {*}
     * @memberof IStyleable
     */
    style?: any;
}
